mapper.update:
update t_gift
set amount =amount + #{stock},stock=stock+#{stock}
where g_id = #{gId}

update t_gift
set status=#{status}
where g_id = #{gId}

update t_gift
set output=output+#{numberr},stock=stock-#{numberr}
where g_id = #{gId} and status="0"

mapper.select:
select * from t_gift
where 1=1

    and name like #{name}


    and `g_id`=#{gId}


    and status=#{status}
